sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //if state onhold
   if(newValue == 3){

	    g_form.setMandatory("x_58872_needit_set_onhold_at", true);

		//all other states
	}else if (newValue != 3){

		g_form.setMandatory("x_58872_needit_set_onhold_at", false);

		g_form.setDisplay("x_58872_needit_set_onhold_at", false);
		g_form.setDisplay("x_58872_needit_substate_onhold", false);
		g_form.setDisplay("hold_reason", false);

		g_form.clearValue('x_58872_needit_set_onhold_at');
		g_form.clearValue('hold_reason');
		g_form.clearValue('x_58872_needit_substate_onhold');

	}
}
