sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var holdReason = g_form.getIntValue('hold_reason');
	//jslog("Me: " + holdReason);

	var checkOnholdDateLimit = new GlideAjax('IncidentOnholdDateUtil_Ajax');
	checkOnholdDateLimit.addParam('sysparm_name','allowedDateRange');
	checkOnholdDateLimit.addParam('sysparm_onholdDate', newValue);

	checkOnholdDateLimit.addParam('sysparm_holdReason', holdReason);

	// Send the request to the server
	checkOnholdDateLimit.getXML(populateOnholdDateField);

	// When the response is back from the server
	function populateOnholdDateField(response){

		var dateFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");

		//TODO: add annotation/info field to inform user
		if(dateFromScriptInclude != newValue){
			g_form.clearValue('x_58872_needit_set_onhold_until');
			g_form.setValue('x_58872_needit_set_onhold_until', dateFromScriptInclude);
			g_form.addInfoMessage('The maximum duration for this onhold reason was exceeded and the value changed to the maximum date/time.');
		}
	}
}
