sys_script_include.script:
var IncidentOnholdDateUtil = {
	allowTimeframe: function(maxNoDays, current){
		//only compare date
		var actualDate = new GlideDate();
		var maxOnholdDate =  new GlideDate();
		var actualOnHoldDateTime = new GlideDateTime(current.u_set_onhold_at);
		var actualOnHoldDate = actualOnHoldDateTime.getLocalDate();

		//add days to local time
		maxOnholdDate.addDaysLocalTime(maxNoDays);

		if(current.state == 3 && actualOnHoldDate.after(maxOnholdDate) ){
			//Write back to db
			actualOnHoldDateTime = maxOnholdDate + ' 12:00:00';

			//datum auf maxOnhold setzen - Datetime?
			var tf_setOnhold = 'u_set_onhold_at';

			current.setValue(tf_setOnhold, actualOnHoldDateTime);
			current.update(); 

		}
	}

};
