sys_script_include.script:
var IncidentOnholdDateUtil_Ajax = Class.create();
IncidentOnholdDateUtil_Ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	/*
	*
	*
	*/
	allowedDateRange: function() {
	  var actualOnholdDateTime = new GlideDateTime(this.getParameter('sysparm_onholdDate'));
      var maxOnholdDateTime = new GlideDateTime();
	  var holdReason = this.getParameter('sysparm_holdReason');
	  var dateRange = 0;


		if(holdReason == 2){
			//pending customer
		dateRange = 8;
	}else{
		//other then pending
		dateRange = 28;
	}

	  //add days depending on onhold reason
	  maxOnholdDateTime.addDaysLocalTime(dateRange);

	  if(actualOnholdDateTime.after(maxOnholdDateTime)){
		//some logic
		return maxOnholdDateTime;
	  }else return actualOnholdDateTime;
    },
	setDateTime: function() {
		var actualDateTime = new GlideDateTime();
		return actualDateTime;
	},
    type: 'IncidentOnholdDateUtil_Ajax'
});
